(381, 1426, 'en', 'tab2_content', '<table border="\\&quot;0\\&quot;" cellpadding="\\&quot;0\\&quot;" cellspacing="\\&quot;0\\&quot;" style="border: 1px dotted rgb(211, 211, 211); color: rgb(34, 34, 34); font-family: Arial, Verdana, sans-serif; font-size: 12px; line-height: normal;" width="\\&quot;518\\&quot;">\r\n	<tbody>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Laser Type</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">EF Technology</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Wavelength</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">1064nm</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Pulse Frequency</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">CW, 0.1&ndash;500kHz</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>CW, 0.1&ndash;500kHz</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">10,000 mm.s-1</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Operating Temperature</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">15oC&ndash;40oC (non condensing)</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Max. Laser Average Power</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">Up to 12W</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Max. Pulse Energy</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">0.2mJ (I) and 0.4mJ (II &amp; II*)</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Max. Peak Power</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">10kW (I) 20kW (II &amp; II*)</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Pulse Duration</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">25ns (10kHz)</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Power Stability</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">&plusmn;3%</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Beam Quality</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">&lt;3mm.mrad (M2 &lt;2)</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Control Electronics</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">19 inch rack mounted (5U) module</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Optical Unit Protection</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">IP52</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Supply Requirement</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">Single phase + Earth, 50 or 60Hz; 100 - 240V. Power 250W</td>\r\n		</tr>\r\n		<tr>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);"><strong>Weight</strong></td>\r\n			<td style="border: 1px dotted rgb(211, 211, 211);">Laser 12Kg, Control Unit 21Kg</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n'),
